import React from 'react';
import { Link } from "react-router-dom";
import { json, checkStatus } from './utils';

const Movie = (props) => {
  const {
    Title,
    Year,
    imdbID,
    Type,
    Poster,
  } = props.movie;

  return (
    <div className="row">
      <div className="col-4 col-md-2 col-lg-1 mb-3">
        <Link to={`/movie/${imdbID}/`}>
          <img src={Poster} className="img-fluid" alt=""/>
        </Link>
      </div>
      <div className="col-8 col-md-10 col-lg-11 mb-3">
        <Link to={`/movie/${imdbID}/`}>
          <h4>{Title}</h4>
          <p>{Type} | {Year}</p>
        </Link>
      </div>
    </div>
  )
}

class MovieFinder extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      results: [],
      error: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ searchTerm: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    let { searchTerm } = this.state;
    searchTerm = searchTerm.trim();
    if (!searchTerm) {
      return;
    }

    fetch(`https://www.omdbapi.com/?s=${searchTerm}&apikey=b7da8d63`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        if (data.Response === 'False') {
          throw new Error(data.Error);
        }
        if (data.Response === 'True' && data.Search) {
          console.log(data);
          this.setState({ results: data.Search, error: '' });
        }
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.log(error);
      })
  }

  render() {
    const { searchTerm, results, error } = this.state;
    return (
      <div className="background">
        <div className="container pt-5">

          <div className="convert-wrap py-4">
            <div className="row convert-inner">
              <div className="col-5">
                <div className="row">
                  <div className="col-6  convert-box mx-4 px-0">
                    <h6>You Have</h6>
                    <input className="currency-input" value="1" type="number" />
                  </div>
                  <div className="col-6  convert-box">
                    <h6 className="currency-name text-center">Euro</h6>
                    <div class="dropdown">
                      <button class="btn dropdown-toggle currency-button" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                        EUR
                      </button>
                      <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                        <li><a class="dropdown-item" href="#">Action</a></li>
                        <li><a class="dropdown-item" href="#">Another action</a></li>
                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-2"></div>
              <div className="col-5">
                <div className="row">
                  <div className="col-6 convert-box">
                    <h6>You Get</h6>
                    <input className="currency-input" value="1" type="number" />
                  </div>
                  <div className="col-6 text-center  convert-box">
                    <h6 className="currency-name text-center">British Pound Sterling</h6>
                    <div class="dropdown">
                      <button class="btn dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                        GBP
                      </button>
                      <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                        <li><a class="dropdown-item" href="#">Action</a></li>
                        <li><a class="dropdown-item" href="#">Another action</a></li>
                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>




          <div className="row">
            <div className="col-12">
              <form onSubmit={this.handleSubmit} className="form-inline my-4">
                <input
                  type="text"
                  className="form-control mr-sm-2"
                  placeholder="frozen"
                  value={searchTerm}
                  onChange={this.handleChange}
                />
                <button type="submit" className="btn btn-primary">Submit</button>
              </form>
              {(() => {
                if (error) {
                  return error;
                }
                return results.map((movie) => {
                  return <Movie key={movie.imdbID} movie={movie} />;
                })
              })()}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default MovieFinder;
