// RatesTable.js
import React from 'react';
import { Link } from "react-router-dom";
import currencies from './utils/currencies';

// We write RatesTable as a render function, since we don't need state or life cycle methods.

// It will receive two props, base and rates. There is an early return statement to not render anything when rates is null, because the rates state is set as null in the constructor of "Rates.js".

changeBase = (event) => {
  this.setState({ base: event.target.value });
  this.getRatesData(event.target.value);
}

const RatesTable = (props) => {
  const { base, rates } = props;
  if (!rates) {
    return null;
  }
  return (
    <React.Fragment>
    <table className="table table-sm bg-light mt-4">
      <thead>
        <tr>
          <th scope="col"></th>
          <th scope="col" className="text-right pr-4 py-2">1.00 {base}</th>
        </tr>
      </thead>
      <tbody>
        {rates.map(currency =>
          <tr key={currency.acronym}>

            <td className="pl-4 py-2"><img src={currencies[base].flag} onChange={this.changeBase} className="currency-flag align-self-center" alt=""/></td>

            <td className="pl-4 py-2 align-middle">{currency.acronym}</td>

            <td className="pl-4 py-2 align-middle">{currency.name}</td>

            <td className="text-right pr-4 py-2 align-middle"><Link to={`/currencyconverter?base=${base}&quote=${currency.acronym}`}>{currency.rate.toFixed(6)}</Link></td>
          </tr>
        )}
      </tbody>
    </table>
    </React.Fragment>
  )
}

export default RatesTable;
