import React from 'react';
import { Link } from "react-router-dom";
import { json, checkStatus } from './utils';

const Movie = (props) => {
  const {
    Title,
    Year,
    imdbID,
    Type,
    Poster,
  } = props.movie;

  return (
    <div className="row">
      <div className="col-4 col-md-2 col-lg-1 mb-3">
        <Link to={`/movie/${imdbID}/`}>
          <img src={Poster} className="img-fluid" alt=""/>
        </Link>
      </div>
      <div className="col-8 col-md-10 col-lg-11 mb-3">
        <Link to={`/movie/${imdbID}/`}>
          <h4>{Title}</h4>
          <p>{Type} | {Year}</p>
        </Link>
      </div>
    </div>
  )
}

class CurrencyConverter extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      rate: 0.89,
      usd: 1,
      euro: 1 * 0.89,
    };

    this.handleUsdChange = this.handleUsdChange.bind(this);
    this.handleEuroChange = this.handleEuroChange.bind(this);
  }

  toUsd(amount, rate) {
    return amount * (1 / rate);
  }

  toEuro(amount, rate) {
    return amount * rate;
  }

  convert(amount, rate, equation) {
    const input = parseFloat(amount);
    if (Number.isNaN(input)) {
      return '';
    }
    return equation(input, rate).toFixed(3);
  }

  // ...toUsd, toEuro
   handleUsdChange(event) {
    const euro = this.convert(event.target.value, this.state.rate, this.toEuro);
    this.setState({
      usd: event.target.value,
      euro
    });
  }
  handleEuroChange(event) {
    const usd = this.convert(event.target.value, this.state.rate, this.toUsd);
    this.setState({
      euro: event.target.value,
      usd
    });
  }

  render() {
    const { rate, usd, euro } = this.state;
    return (
      <div className="background">
        <div className="container pt-5">

          <div className="convert-wrap py-4">
            <div className="row convert-inner">
              <div className="col-6">
                <div className="row">
                  <div className="col-6  convert-box">
                    <h6>You Have</h6>
                    <input className="currency-input" value="1" type="number" />
                  </div>
                  <div className="col-6 text-center convert-box">
                    <h6 className="currency-name text-center">Euro</h6>
                    <div class="dropdown">
                      <button class="btn dropdown-toggle currency-button" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                        EUR
                      </button>
                      <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                        <li><a class="dropdown-item" href="#">Action</a></li>
                        <li><a class="dropdown-item" href="#">Another action</a></li>
                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-6">
                <div className="row justify-content-end">
                  <div className="col-6 convert-box">
                    <h6>You Get</h6>
                    <input className="currency-input" value="1" type="number" />
                  </div>
                  <div className="col-6 text-center convert-box">
                    <h6 className="currency-name">British Pound Sterling</h6>
                    <div class="dropdown">
                      <button class="btn dropdown-toggle currency-button" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                        GBP
                      </button>
                      <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                        <li><a class="dropdown-item" href="#">Action</a></li>
                        <li><a class="dropdown-item" href="#">Another action</a></li>
                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    )
  }
}

export default CurrencyConverter;
